# Write your MySQL query statement below
SELECT A.student_id,A.student_name , C.subject_name,COUNT(B.subject_name) as attended_exams FROM Students A JOIN Subjects C LEFT JOIN Examinations B ON A.student_id=B.student_id and C.subject_name = B.subject_name  GROUP BY A.student_id,C.subject_name ORDER BY A.student_id,C.subject_name ;